import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../main_shortcuts/shortcut.scss";

const Shortcut = () => {
  const [data, setData] = useState(null); // State to store the fetched data

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("https://api.testvalley.kr/collections", {
          params: {
            type: "SINGLE",
            viewType: "TILE"
          }
        });

        setData(res.data.items); 
        console.log(res.data.items.items.publication.media.uri)
        // Update state with fetched data items
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData(); // Call fetchData function when component mounts
  }, []); // Empty dependency array ensures useEffect runs only once when component mounts

  return (
    <div className='container'>
      {data ? (
        data.map((item, index) => (
          <div key={index} className='item'>
            <div className="item-content">
              {/* Render item title and subtitle */}
              <p className='item-title'>{item.title}</p>
              <p className='item-subtitle'>{item.subtitle}</p>
            </div>
            {/* Loop through media and render images */}
            <div className="media-container">
              {item.items && item.items.some(item => item.publication && item.publication.media) &&
                item.items
                  .filter(mediaItem => mediaItem.publication && mediaItem.publication.media)
                  .map((mediaItem, mediaIndex) => (
                    <div key={mediaIndex} className="media-item">
                      {/* Accessing media array inside publication */}
                      {mediaItem.publication.media.map((media, index) => (
                        <img key={index} src={media.uri} alt='' />
                      ))}
                    </div>
                  ))}
            </div>
          </div>
        ))
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Shortcut;
