import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../main_shortcuts/shortcut.scss";

const Shortcut = () => {
  const [data, setData] = useState(null); // State to store the fetched data

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("https://api.testvalley.kr/collections?prearrangedDiscount&type=SINGLE&viewType=TILE");
        setData(res.data.items); // Update state with fetched data items
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData(); // Call fetchData function when component mounts
  }, []); // Empty dependency array ensures useEffect runs only once when component mounts

  return (
    <div className='container'>
      {data ? (
        data.map((item, index) => (
          <div key={index} className='item'>
            {/* Render item title */}
            <p>{item.title}</p>
            {/* Loop through media and render fileName */}
            {item.items.map((mediaItem, mediaIndex) => (
              <div key={mediaIndex}>
                <p>File Name: {mediaItem.fileName}</p>
              </div>
            ))}
          </div>
        ))
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Shortcut;
