import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../main_shortcuts/shortcut.scss";

const Shortcut = () => {
  const [data, setData] = useState(null); // State to store the fetched data

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("https://api.testvalley.kr/collections" ,{
            params: {
              type: "SINGLE",
              viewType: "TILE"
            }
          });

        setData(res.data.items); 
        console.log(res.data.items.items.publication.media.uri)
        // Update state with fetched data items
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData(); // Call fetchData function when component mounts
  }, []); // Empty dependency array ensures useEffect runs only once when component mounts

  return (
    <div className='container'>
      {data ? (
        data.map((item, index) => (
          <div key={index} className='item'>
            {/* Render item title */}
            <p>{item.title}</p>
            {/* Loop through media and render fileName */}
            {item.items.map((mediaItem, mediaIndex) => (
              <div key={mediaIndex}>
              <p>  {mediaItem.publication.title}</p>
              {/* Accessing media array inside publication */}
              {mediaItem.publication && mediaItem.publication.media && (
  <div>
    {mediaItem.publication.media.map((mediaItem, index) => (
      <img key={index} src={mediaItem.uri} alt='' />
    ))}
  </div>
)}


              {/* <img src={mediaItem.uri} alt={mediaItem.fileName} />       */}
                        {/* <img src={item.imageUrl} alt="" /> */}
              </div>
            ))}
          </div>
        ))
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Shortcut;
